INSERT INTO `rr2008_msgs` VALUES (3, '2008-01-03 00:00:00', 'Nileshwar, India', ' \r\n', 12.25, 75.1, NULL, NULL, 0, NULL, 2403, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (4, '2008-01-03 00:00:00', 'Mangalore, India', ' \r\n', 12.8694, 74.8426, NULL, NULL, 0, NULL, 2883, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (5, '2008-01-04 00:00:00', 'Palolem Beach, India', ' \r\n', 15.01, 74.0232, NULL, NULL, 0, NULL, 4669, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (6, '2008-01-05 06:36:00', 'Morgim Beach, Goa, India', 'leaving goa, heading north for mumbai\r\n', 15.6154, 73.7364, NULL, NULL, 0, NULL, 5348, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (7, '2008-01-06 04:03:00', 'Sangameshwar, Maharashtra, India', 'spent the night in sangameshwar heading for pune tonight, hopefully a little sightseeing along the way\r\n', 17.1871, 73.552, NULL, NULL, 0, NULL, 7663, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (8, '2008-01-06 11:45:00', 'Pune, Maharashtra, India', 'Explored Fort Pratapgad this afternoon. Dinner with Alex'' old coworker tonight in Pune. Headed for caves of Ellora tomorrow. Making 60+ kph on NH4!\r\n', 18.5205, 73.8566, NULL, NULL, 0, NULL, 10462, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (9, '2008-01-07 06:44:00', 'Pune, Maharashtra, India', 'Spent night in pune, off to the ellora caves now. Traffic in pune ridiculous... Drove across a field at one point!\r\n', 18.5205, 73.8566, NULL, NULL, 0, NULL, 10462, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (10, '2008-01-08 03:42:00', 'Aurangabad, Maharashtra, India', 'Saw "Poor Man''s Taj Mahal" in dawn light. Minor rickshaw repair. Now en route to Ajanta Caves - Ellora Caves closed Tuesdays :( Delicious samosas!\r\n', 19.8853, 75.3197, NULL, NULL, 0, NULL, 11311, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (11, '2008-01-08 14:32:00', 'Ajanta Caves, Maharashtra, India', 'Decided to see *real* Taj. Agra detour adds 3 days. Lamorna we won''t be too late. Escorted Erandol 3 star police chief to station and home. Kingfisher!\r\n', 20.5329, 75.7502, NULL, NULL, 0, NULL, 11778, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (12, '2008-01-09 06:06:00', 'Indore, Madhya Pradesh, India', 'both rickshaws got water in gas tanks this morning - 3hr delay to clean carburators... Still heading for Agra, 850k! A bit further than expected...\r\n', 22.7271, 75.8665, NULL, NULL, 0, NULL, 13380, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (13, '2008-01-10 03:48:00', 'Dewas, Madhya Pradesh, India', 'Actually 100km west in Dewas at a Bajaj dealer buying a gas cap (oops) and getting a "black smoke is coming from your engine" service. Cross fingers\r\n', 22.9597, 76.0491, NULL, NULL, 0, NULL, 13488, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (14, '2008-01-10 04:14:00', 'Dewas, Madhya Pradesh, India', 'Actually 150k se of bohpal, night in Indore. Lost a gas cap + think cyrus'' is leaking a little oil, repairs now. Taj Mahal closed fri, aim for sat.\r\n', 22.9597, 76.0491, NULL, NULL, 0, NULL, 13488, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (15, '2008-01-10 17:06:00', 'Shivpuri, Madhya Pradesh, India', 'Stopped for the night in Shivpuri, 100km from Gwalior. Elephants and camels and herds of sheep oh my! Late night engine problems oh no! To the Taj!\r\n', 25.4233, 77.6536, NULL, NULL, 0, NULL, 14373, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (16, '2008-01-12 01:13:00', 'Agra, Uttar Pradesh, India', 'Watching the sun rise over the taj mahal in agra! It is quite spectacular... \r\n', 27.1777, 78.0093, NULL, NULL, 0, NULL, 15076, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (17, '2008-01-12 05:04:00', 'Agra, Uttar Pradesh, India', 'Saw the Taj Mahal in Agra this morning Awesome and well worth 3 day detour! Leaving this afternoon, east to varanesi, reattach muffler 1st... Again!\r\n', 27.1777, 78.0093, NULL, NULL, 0, NULL, 15076, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (18, '2008-01-13 04:22:00', 'Auraiya, Uttar Pradesh, India', 'Spent the night in Auraiya and breakfast in Kanpur - masala omlets, chai, and samosas. Will meet up with Tommy (Chapati Chasers) in Varnasi tonight.\r\n', 26.464, 79.5065, NULL, NULL, 0, NULL, 15583, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (19, '2008-01-13 10:32:00', 'Allahabad, Uttar Pradesh, India', 'Minor delays to clean and regap sparkplug then lash roof rack together. Got hotel reservations in Varnasi so we''re going to get there! 130 km to go.\r\n', 25.443, 81.8284, NULL, NULL, 0, NULL, 16604, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (20, '2008-01-14 16:42:00', 'Varanasi, Uttar Pradesh, India ', 'Spent whole day in varanesi, team dave sick - recovering now, should be good to go tomorrow, planning on heading for gaya.\r\n', 25.282, 82.9563, NULL, NULL, 0, NULL, 16995, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (21, '2008-01-15 11:38:00', 'Varanasi, Uttar Pradesh, India', 'Finally leaving varenasi along with chapati chasers after frustrating day of repairs/delays. Hopefully on the right road to gaya!\r\n', 25.282, 82.9563, NULL, NULL, 0, NULL, 16995, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (22, '2008-01-15 13:22:00', 'Mohania, Bihar, India', 'Just crossed over into Bihar, asked to pay 200 rupee ''tax'' - told border guards to go to hell and drove thru! They dinn''t really seem to care...\r\n', 25.1673, 83.6215, NULL, NULL, 0, NULL, 17264, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (23, '2008-01-16 03:34:00', 'Aurangabad, Bihar, India', 'There''s a sizeable hole in our piston... Eta to get back on the road: about 2hrs! Spent the night in araungabad, bihar after finding hotel at 1am.\r\n', 24.7484, 84.3767, NULL, NULL, 0, NULL, 17477, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (24, '2008-01-17 10:00:00', 'Gaya, Bihar, India', 'all cars back on the road, heading for nepal border, day and a half to go. Alex taking detour to dehli for friend''s wedding, meeting back up at border.\r\n', 24.7911, 84.9964, NULL, NULL, 0, NULL, 17622, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (25, '2008-01-17 15:46:00', 'Purnia, Bihar, India', 'Actually 100km NE in Purnia in dry hotel. Dropped case of beer. Then the power went out. Here comes room service. Darjeeling tomorrow.\r\n', 25.7762, 87.4661, NULL, NULL, 0, NULL, 18727, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (26, '2008-01-18 13:05:00', 'Darjeeling, West Bengal, India', 'Really Darjeeling. Heavy fog in morning but pushed on and up epic motorway clinging to hillside to make high tea. Mist blocked sunset and Everest :(\r\n', 27.04, 88.2636, NULL, NULL, 0, NULL, 20209, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (27, '2008-01-20 00:48:00', 'Kakarbhitta, Nepal', 'Nepal! Last ones across the border. Staying in Kakarbvitta. Ricks repaired. Children taken joyriding. "Shortcut" down from Darjeeling steep and twisty.\r\n', 26.6523, 88.156, NULL, NULL, 0, NULL, 20242, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (28, '2008-01-20 16:22:00', 'Janakpur, Nepal', 'Actually janagpur. Found Slepak and lots of political demonstrations. Ricks on last legs, holding! Thru the mtns in the morning! Black label!\r\n', 26.7327, 85.9248, NULL, NULL, 0, NULL, 20243, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (29, '2008-01-21 00:00:00', 'Hetauda, Nepal', ' \r\n', 27.4167, 85.0333, NULL, NULL, 0, NULL, 20244, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (30, '2008-01-21 15:40:00', 'Kathmandu, Nepal', 'Fin. Or at least close enough to taste dinner at Rum Doodle. 5300km and she''s still running strong, though a bit louder than she used to.\r\n', 27.7029, 85.3182, NULL, NULL, 0, NULL, 20245, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (31, '2008-01-22 09:24:00', 'Kathmandu, Nepal', 'Stoned in kathmandu! Trying to return rickshaws... Ran into protests! Had to divert, currently waiting for things to calm down!\r\n', 27.7029, 85.3182, NULL, NULL, 0, NULL, 20245, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (32, '2008-01-22 15:09:00', 'Kathmandu, Nepal', 'Three hr delay, but have now officially returned both the rickshaws and the paperwork! 5399.5km and we''re done!', 27.7029, 85.3182, NULL, NULL, 0, NULL, 20245, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (89, '2007-12-30 05:55:00', NULL, 'I''m writing this from the Orion Homestay where Slepak, Logan and I have just checked in.  Supposedly Cyus has a room in the hotel next door.  We''re in one piece and got suitably excited about driving on Indian roads on the ride from the airport.  Next up lunch and some practice sessions on a real rickshaw!&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-443590848015669469?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'The Beginning', 'http://safety3rd.blogspot.com/2007/12/beginning.html', 1, 'dlott', NULL, 'Kochi', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (90, '2007-12-30 12:01:00', NULL, 'Trying to see if we can blog from our cellphones...&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-7834367001207967907?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Hanging out in kochi', 'http://safety3rd.blogspot.com/2007/12/hanging-out-in-kochi.html', 1, 'Alex', NULL, 'Kochi', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (91, '2007-12-31 02:44:00', NULL, '&lt;a href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/R3hZNFDaOPI/AAAAAAAADNc/K1n6XM1YUtk/s1600-h/upload1.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5149964255127812338&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/R3hZNFDaOPI/AAAAAAAADNc/K1n6XM1YUtk/s400/upload1.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/R3hZNFDaOQI/AAAAAAAADNk/svTRIrz_w6s/s1600-h/upload2.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5149964255127812354&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/R3hZNFDaOQI/AAAAAAAADNk/svTRIrz_w6s/s400/upload2.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;div&gt;We got our first glance at the rickshaws yesterday - haven''t actually seen ours yet, but there were a dozen or so parked for us to see, I suppose there were mostly the ones that had ordered paint jobs (we didn''t). They seem kinda nice - at the very least they''re brand new, and seem to have a more functional suspension than some of the ones I''ve ridden in over the past week. 2 stroke engines seem like they could become a pain in the ass, but we''ll see, and we do have a stereo system!&lt;/div&gt;&lt;br /&gt;&lt;div&gt;&lt;/div&gt;&lt;br /&gt;&lt;div&gt;&lt;/div&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-5185201465559314083?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'First glance at the rickshaws', 'http://safety3rd.blogspot.com/2007/12/first-glance-at-rickshaws.html', 1, 'Alex', NULL, 'Kochi', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (92, '2007-12-31 13:16:00', NULL, 'We''ve driven the rickshaws and they work!  I''m currently sitting in Slepak and Lott''s room mooching free wireless as we wait for the ferries to take us to the island for the party tonight and figured I would make my first post and give a brief update of the status here.  Lott and Slepak''s rickshaw was delayed in arrival until late this afternoon so we spent the afternoon driving our rickshaw in loops around the parade ground.  Each of us almost made it around the ground without stalling out!  For some reason our rickshaw is no longer starting, but we''re assuming that''s just because it''s out of petrol at the moment and we''ll deal with that situation in the morning before we leave (we''re hoping that''s the problem at least).  Anywho, looks like we''re leaving.  Last post of 2007...  Happy New Year!&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-536989960411721731?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'The Rickshaws Run!', 'http://safety3rd.blogspot.com/2007/12/rickshaws-run.html', 1, 'Logan', NULL, 'Kochi', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (93, '2008-01-01 03:36:00', NULL, '&lt;a href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/R3m1b1DaORI/AAAAAAAADNs/ZPApYsJjga4/s1600-h/logan.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5150347138577348882&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/R3m1b1DaORI/AAAAAAAADNs/ZPApYsJjga4/s400/logan.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt; Logan adding decoration to the rickshaw - don''t run with scissors!&lt;br /&gt;&lt;div&gt;&lt;/div&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-2972295200428857077?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Safety Third!', 'http://safety3rd.blogspot.com/2007/12/safety-third.html', 1, 'Alex', NULL, 'Kochi', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (94, '2008-01-02 15:55:00', NULL, '2 days of driving the mighty rickshaws has put up into a rather nice hotel in nileshwar, india. no particularly serious issues with the cars so far, just not making quite as good me a time as we had hoped, but still ok. more later!&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-7421091809077252893?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, '2 days of driving done ', 'http://safety3rd.blogspot.com/2008/01/2-days-of-driving-done.html', 1, 'Alex', NULL, 'Nileshwar', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (95, '2008-01-05 14:13:00', NULL, 'Can''t remember the name of the town we''re currently in, but we have a dial-up internet connection, so a quick update. Things going well so far, after five days of driving, the rickshaws are still alive (had a tune-up this morning and fixed a broken roof rack on ours), and we are a day away from Mumbai. It looks like most teams have gone East, but we decided to head to Mumbai and try to see the caves in Ellora/Ajanta. Unfortunately, it doesn''t look like we''ll have time for Agra (Taj Mahal), so I''ll just have to come back after Nepal.&lt;br /&gt;&lt;br /&gt;Spent most of the day yesterday in Goa, hanging out on the gorgeous beaches with a few other teams and the Rickshaw Run video crew; went swimming in the Indian ocean! Lots of stories and pictures queued up, but nee better internet! Should be somewhere near Mumbai by tomorrow night, hopefully we''ll solve that problem then...&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-3742166406308800212?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Heading North!', 'http://safety3rd.blogspot.com/2008/01/heading-north.html', 1, 'Alex', NULL, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (96, '2008-01-06 20:02:00', NULL, 'We''ve reached the city of Pune, India, population 3.8 million, which makes for some absolutely insane traffic conditions! But we made it through! Original plan had been to go to Mumbai, but we decided to divert to Pune, since it''s more on the way, and we were terrified to drive through Mumbai, an even bigger city!&lt;br /&gt;&lt;br /&gt;In Pune, met up with Michelle, who is a former co-worker of mine, currently traveling around India. She''s being nice about our rickshaw-driving exploits, but I''m sure she finds us to be just as crazy as everyone else does for doing this! But that''s ok, we''re good at doing crazy! On the bright side, the first words out of her mouth upon seeing the rickshaws were ''What''s first and second?''&lt;br /&gt;&lt;br /&gt;Currently staying at an extrmenly posh La Meridian hotel in downtown Pune, which costs16,000 rupees a night! ($400), but Cyrus is getting for free via points... stark (really, really stark) contrast from our lodgings the last three nights! Tomorrow, taking off towards Ellora and Ajanta caves, which are supposed to be amazing, and we''ll hopefully see on Tuesday...&lt;br /&gt;&lt;br /&gt;On the less exciting side of things, just heard from Tommy, of the Chipatti Chasers team (that being the Brits we knew from the Mongol Rally), and they had apparently fliped their rickshaw (again... Mongol Rally also ended in a similar fashion). Nobody was hurt, fortunately, not clear if any of them will continue...&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-4263673896041671028?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Skipping Mumbai, heading to Pune', 'http://safety3rd.blogspot.com/2008/01/skipping-mumbai-heading-to-pune.html', 1, 'Alex', NULL, 'Pune', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (97, '2008-01-06 20:12:00', NULL, 'We''re mastering technology (a little), as we''ve figured out how to send text messages to the Rickshaw Run website with brief updates. They should all show up here: &lt;a href=&quot;http://rickshawrun08w.theadventurists.com/index.php?mode=team&amp;amp;sub=display&amp;amp;pagemode=ontheroad&amp;amp;name=SafetyThird&quot;&gt;http://rickshawrun08w.theadventurists.com/index.php?mode=team&amp;amp;sub=display&amp;amp;pagemode=ontheroad&amp;amp;name=SafetyThird&lt;/a&gt;.&lt;br /&gt;&lt;br /&gt;Now that I''ve announced this, I guess we''ll have to make a conscious effort to keep up updates!&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-984334646936606651?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'SMS messages to the website', 'http://safety3rd.blogspot.com/2008/01/sms-messages-to-website.html', 1, 'Alex', NULL, '', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (98, '2008-01-06 20:17:00', NULL, '&lt;a href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/R4E321DaOSI/AAAAAAAADN0/bxqjOFRpg94/s1600-h/upload1.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5152460863782402338&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/R4E321DaOSI/AAAAAAAADN0/bxqjOFRpg94/s400/upload1.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/R4E33FDaOTI/AAAAAAAADN8/37-76G0qMOg/s1600-h/upload2.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5152460868077369650&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/R4E33FDaOTI/AAAAAAAADN8/37-76G0qMOg/s400/upload2.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;div&gt;Spent a full day at Goa beach - swimming, hanging out on the sand, and tossing a frisbee around!&lt;/div&gt;&lt;br /&gt;&lt;div&gt;&lt;/div&gt;&lt;br /&gt;&lt;div&gt;&lt;/div&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-2818625582440305071?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Beautiful Goa!', 'http://safety3rd.blogspot.com/2008/01/beautiful-goa.html', 1, 'Alex', NULL, 'Palolem Beach', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (99, '2008-01-07 16:27:00', NULL, 'We''re currently in an iWay internet cafe in Aurangabad where we are spending the night and preparing for a day of site seeing tomorrow at the Ajanta caves about 100km northeast of the city. We had planned on seeing the caves at Ellora as well (20km northwest of Aurangabad), but apparently those are closed on Tuesday so we''ll just head to Ajanta, and with the extra time perhaps we''ll be able to stop and see the fort along the way.&lt;br /&gt;&lt;br /&gt;Speaking of forts, Protapgad fort outside of Mahabaleshwar was amazing. On the way to Pune we drove through Mahabaleshwar, which is a city high up in the Western Ghats about 1300m up or so. About 20km outside the city, there was a fort at the very top of a hill that surveyed the entire surrounding mountain ranges. (Yes Varun, we went through the mountains. I know that was the one piece of advice you gave us to be careful about and avoid, but we survived, it was beautiful, and the driving was good practice for climbing the hills in Nepal I''m sure). I''ll try and get Alex to post some pictures from the fort sometime soon....&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-2775636990431266897?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Over the mountains and to Aurangabad', 'http://safety3rd.blogspot.com/2008/01/over-mountains-and-to-aurangabad.html', 1, 'Logan', NULL, 'Aurangabad, Ajanta Caves', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (100, '2008-01-07 16:57:00', NULL, '&lt;a href=&quot;http://2.bp.blogspot.com/_h7P9YS3eqpU/R4Jau9faQXI/AAAAAAAAAAU/xrCisgs11KQ/s1600-h/inside.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5152780686492057970&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://2.bp.blogspot.com/_h7P9YS3eqpU/R4Jau9faQXI/AAAAAAAAAAU/xrCisgs11KQ/s320/inside.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a href=&quot;http://4.bp.blogspot.com/_h7P9YS3eqpU/R4JavdfaQYI/AAAAAAAAAAc/n1BAtyA5yhA/s1600-h/logan.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5152780695081992578&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://4.bp.blogspot.com/_h7P9YS3eqpU/R4JavdfaQYI/AAAAAAAAAAc/n1BAtyA5yhA/s320/logan.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a href=&quot;http://1.bp.blogspot.com/_h7P9YS3eqpU/R4JavtfaQZI/AAAAAAAAAAk/vF_tu4Ujlp8/s1600-h/meridian.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5152780699376959890&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://1.bp.blogspot.com/_h7P9YS3eqpU/R4JavtfaQZI/AAAAAAAAAAk/vF_tu4Ujlp8/s320/meridian.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a href=&quot;http://1.bp.blogspot.com/_h7P9YS3eqpU/R4JavtfaQaI/AAAAAAAAAAs/8nyzgwrwTK0/s1600-h/rockstar.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5152780699376959906&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://1.bp.blogspot.com/_h7P9YS3eqpU/R4JavtfaQaI/AAAAAAAAAAs/8nyzgwrwTK0/s320/rockstar.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a href=&quot;http://2.bp.blogspot.com/_h7P9YS3eqpU/R4Jav9faQbI/AAAAAAAAAA0/jzHkucCp8b0/s1600-h/slepak.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5152780703671927218&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://2.bp.blogspot.com/_h7P9YS3eqpU/R4Jav9faQbI/AAAAAAAAAA0/jzHkucCp8b0/s320/slepak.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;div&gt;&lt;a href=&quot;http://1.bp.blogspot.com/_h7P9YS3eqpU/R4JabtfaQWI/AAAAAAAAAAM/e3dHA475aG8/s1600-h/cyrus.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5152780355779576162&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://1.bp.blogspot.com/_h7P9YS3eqpU/R4JabtfaQWI/AAAAAAAAAAM/e3dHA475aG8/s320/cyrus.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;Alright, well, pictures of fort will have to wait but here are some pictures of the mountains, pictures at Le Meridien (in picture number 1 we had just discovered that there was not only a 42inch HDTV in the entry way, but there was also another 42 inch TV in the bedroom as well), and a few others with the locals.  Time to go give our passport information to the guy running the hotel so he can go to sleep, and probably time that we went to sleep as well.&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-4988968268067695774?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Photos', 'http://safety3rd.blogspot.com/2008/01/photos.html', 1, 'Logan', NULL, 'Pune', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (101, '2008-01-11 13:17:00', NULL, 'with my own eyes. And it is gorgeous. Admittedly it''s much smaller than Logan:&lt;br /&gt;&lt;br /&gt;&lt;a href=&quot;http://4.bp.blogspot.com/_yG3LJbOEQIc/R4duQ4XTUmI/AAAAAAAABo0/ILhYyL4dy-Y/s1600-h/IMG_1883_small.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5154209534835905122&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://4.bp.blogspot.com/_yG3LJbOEQIc/R4duQ4XTUmI/AAAAAAAABo0/ILhYyL4dy-Y/s400/IMG_1883_small.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;div&gt;&lt;/div&gt;&lt;br /&gt;&lt;br /&gt;But hopefully at dawn tomorrow it will look bigger. We''re off to dinner, but should have time to write up some stories this evening...&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-4668644775031871521?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'I have seen the Taj Mahal', 'http://safety3rd.blogspot.com/2008/01/i-have-seen-taj-mahal.html', 1, 'dlott', NULL, 'Agra', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (102, '2008-01-12 00:14:00', NULL, 'While loading up the rickshaws, a local noticed that ours sounded horrible and our muffler was bouncing around a lot.  Apparently the nut that held on the muffler had fallen off and the bolt was busy being worn down to nothing.  After a bit of poking and 5 Rs., we had a new bolt and a quieter rickshaw for our  drive to The Poor Man&acirc;??s Taj Mahal.  It&acirc;??s got the big dome, four towers, and looks cute and manageable.  It was nice going early in the morning so that we had the place mostly to ourselves. Next we headed for  Ajanta, one of two sites in India famous for their cave temples carved into the cliffside (the other is Ellora, but it was closed Tuesdays).  They charged for parking the rickshaw, walking through the tourist shops to the shuttle bus, the shuttle bus to the caves, bag check, admission, the bathroom, and the bus back to the parking lot.  But despite all that it was totally worth it.  Lonely Planet can give you the full rundown on the history of the cave and the artwork they contain, we&acirc;??ll just stick with saying whatever the history is &acirc;?? Buddhist, Hindu, Jainist &acirc;?? they look very impressive! Some highlights &acirc;?? a huge reclining Buddha, a bunch of &acirc;??spider&acirc;?? Buddhas in one of the temples holding up the ceiling, and two story caves&acirc;?&brvbar;&lt;br /&gt;&lt;br /&gt;After the caves, we came to what, I suppose, was an inevitable decision point: do we go to Agra or not? The original plans, had us going to Agra simply because you really can&acirc;??t go to India and not see the Taj Mahal! Then we got to Kochi, talked to everybody else, heard over half were going directly East, and how long they thought the drive was going to take&acirc;?&brvbar; and figured we had to skip Agra in order to stick within our timeline. Wildly guessing on a map, Ajanta is 1100 km from Varanasi directly and 1400 km via Agra, so for the price of one extra day driving we get to see the Taj Mahal Friday!   Two hours of driving in, Lonely Planet brings an enlightening discovery: the Taj Mahal is actually closed on Fridays. (Side Note: why in the world is the Taj Mahal, the greatest tourist attraction in all of India, closed? Ever?) Another brief conversation on the side of the road later, and we&acirc;??re going anyway &acirc;?? we can just spend Friday in Agra, see the Taj Mahal at sunrise on Saturday and still leave for Varinasi before noon, which could get us there in time to spend most of Monday there (with some good fortune in terms  of roads conditions, traffic, etc.)&lt;br /&gt;&lt;br /&gt;Trying to push some extra mileage in the evening we tried to take a back roads short cut around Jalgaon, against the advice of the locals.  That didn&acirc;??t work out so well.  We also ended up being commandeered as a taxi cab for the chief of police of Erandol, who told us his town didn&acirc;??t have any decent lodging.  Not sure if he was just trying to run us out of his town or telling the truth. But we embarked on our first stint of highway night driving to get to Parola for the night.  It&acirc;??s pretty scary when you can see the potholes better by the light of oncoming traffic (in your lane since they&acirc;??re passing someone) than you can by your own headlights&acirc;?&brvbar;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-7125076949120620518?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Day 8', 'http://safety3rd.blogspot.com/2008/01/day-8.html', 1, 'dlott', NULL, 'Ajanta Caves', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (103, '2008-01-12 04:53:00', NULL, '&lt;a href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/R4hICFDaOVI/AAAAAAAADOM/r1fjtJgjHZE/s1600-h/upload.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5154448974078949714&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/R4hICFDaOVI/AAAAAAAADOM/r1fjtJgjHZE/s400/upload.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;div&gt;Actual Logan (tiny dot on the left, I am the even tinier dot on the right) is significantly smaller than the Taj Mahal... The Taj itself, however, is quite awesome, and well worth the detour!&lt;/div&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-6023796067735707861?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Actual Logan', 'http://safety3rd.blogspot.com/2008/01/actual-logan.html', 1, 'Alex', NULL, 'Agra', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (104, '2008-01-13 16:12:00', NULL, 'It seems like every day we set out to make a long haul drive to the next city some mechanical issue crops up, and today was no exception.  Less than a kilometer after pulling out of the petrol station after filling up this morning our rickshaw started losing power, then died, and refused to start again.  When draining the gas and replacing it, and taking the carburetor apart looking at it and putting it back together didn''t solve the problem (Cyrus is quite proud that he was able to take the carburetor apart and put it back together and have it work by the way), we eventually had Lott and Slepak bring us a mechanic from town who discovered that our spark plug had issues.  A little sand papering and cleaning later, along with some tweaks to the engine idle, and we were back in business running fine.  Unfortunately during the fixing process we neglected to keep an eye on our screw driver with replaceable nibs and it &quot;disappeared.&quot;  We''re assuming one of the many kids who were around watching us made off with it.  Our funnel also disappeared, but that one is probably mostly due to team negligence as we had left it on top of the rickshaw during the fixing process and we probably just forgot to take it down before doing the test drive with the mechanic and it fell off somewhere along the way never to be seen again.&lt;br /&gt;&lt;br /&gt;Two lane &quot;super highway&quot; for the next few hours allowed us to make good time most of the afternoon, and we made it into Varanasi and Hotel Divya a little after dark.  We were surprised to discover another rickshaw from the rally parked outside the hotel next door, and they were actually people we had met before.  We ate lunch with &quot;Discovering the Earth&quot; near Khozikide beach while we were still far to the south (on a side note, that lunch featured the best tasting bread we''ve ever had and we''re still trying to figure out what it was that we had so we can order it again).  We tried to convince them to come eat dinner with us again this evening, but they were retiring early so they could see a temple in the morning and then press on through Bihar and toward Kathmandu in the afternoon. &lt;br /&gt;&lt;br /&gt;Speaking of retiring, that is where I am going to head and leave further blog posting in the able hands of Lott and Slepak as we''re planning on waking up very early tomorrow morning in order to catch a boat and watch the sun rise on the river.  I hear there''s also a kite festival hapenning tomorrow.  That should be interesting...&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-872662192838619660?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Varanasi', 'http://safety3rd.blogspot.com/2008/01/varanasi.html', 1, 'Logan', NULL, 'Varanasi', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (105, '2008-01-14 06:43:00', NULL, 'Well we are in Varanasi for the day while Team Dave is ill. Don''t worry we expect a speedy recovery. While we are sitting around though we are stocking up on supplies and we''ll try to post some pictures. The first three are from the Ajunta Caves. Expect some better photo''s later from Alex.  Who knows maybe we''ll go crazy and post some video''s of driving....&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;a href=&quot;http://cyrusgray.us/rickshawrun/ajuntacaves.jpg&quot;&gt;&lt;img style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; WIDTH: 400px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://cyrusgray.us/rickshawrun/ajuntacaves.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a href=&quot;http://cyrusgray.us/rickshawrun/ajuntacaves2.jpg&quot;&gt;&lt;img style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; WIDTH: 400px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://cyrusgray.us/rickshawrun/ajuntacaves2.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a href=&quot;http://cyrusgray.us/rickshawrun/ajuntacaves3.jpg&quot;&gt;&lt;img style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; WIDTH: 400px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://cyrusgray.us/rickshawrun/ajuntacaves3.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;div&gt;&lt;/div&gt;&lt;br /&gt;&lt;div&gt;&lt;/div&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-1134838585200484623?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Ajunta Caves', 'http://safety3rd.blogspot.com/2008/01/ajunta-caves.html', 1, 'Cyrus', NULL, 'Ajanta Caves', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (106, '2008-01-14 07:58:00', NULL, 'As Cyrus promised, a few more shots from Ajanta:&lt;br /&gt;&lt;center&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SvP1heiicfI/AAAAAAAAIdo/FMnLi3HAKQ4/s1600-h/spider.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SvP1heiicfI/AAAAAAAAIdo/FMnLi3HAKQ4/s400/spider.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400930333629772274&quot; /&gt;&lt;/a&gt;Spider Buddha!&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SvP2L4xc6KI/AAAAAAAAId4/s_WUWEE40mk/s1600-h/temple.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SvP2L4xc6KI/AAAAAAAAId4/s_WUWEE40mk/s400/temple.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400931062226151586&quot; /&gt;&lt;/a&gt;in one of the Temples&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SvP2dAhc0oI/AAAAAAAAIeA/TtD9NTqrcS0/s1600-h/logan.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SvP2dAhc0oI/AAAAAAAAIeA/TtD9NTqrcS0/s400/logan.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400931356364296834&quot; /&gt;&lt;/a&gt;&lt;br /&gt;taking a break...&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SvP1ggt7EdI/AAAAAAAAIdY/Y381e4NMibA/s1600-h/break.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SvP1ggt7EdI/AAAAAAAAIdY/Y381e4NMibA/s400/break.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400930317034525138&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;/center&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-6276766303161983845?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'More Pictures', 'http://safety3rd.blogspot.com/2008/01/more-pictures.html', 1, 'Alex', NULL, 'Ajanta Caves', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (107, '2008-01-14 08:12:00', NULL, '&lt;center&gt;First, there was the Mini Taj Mahal:&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SvP4bEF0tRI/AAAAAAAAIeI/k01oOZbN7mg/s1600-h/minitaj.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SvP4bEF0tRI/AAAAAAAAIeI/k01oOZbN7mg/s400/minitaj.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400933521985680658&quot; /&gt;&lt;/a&gt;&lt;br /&gt;Then we got to the real thing (3 days and a few breakdowns later):&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SvP4bQR2ySI/AAAAAAAAIeQ/KPLLD31yfFI/s1600-h/taj.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 300px; height: 400px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SvP4bQR2ySI/AAAAAAAAIeQ/KPLLD31yfFI/s400/taj.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400933525257373986&quot; /&gt;&lt;/a&gt;&lt;br /&gt;Not the best picture, but all four of us at the Taj Mahal:&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SvP4bt-JFwI/AAAAAAAAIeY/KtvsSlHtW0w/s1600-h/all4.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 261px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SvP4bt-JFwI/AAAAAAAAIeY/KtvsSlHtW0w/s400/all4.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400933533227751170&quot; /&gt;&lt;/a&gt;&lt;br /&gt;By the way, it gets cold in the morning!&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SvP4b6G-d8I/AAAAAAAAIeg/c_pgBMa-Dgs/s1600-h/cold.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 297px; height: 400px;&quot; src=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SvP4b6G-d8I/AAAAAAAAIeg/c_pgBMa-Dgs/s400/cold.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400933536486029250&quot; /&gt;&lt;/a&gt;&lt;br /&gt;Cyrus has us staying in style once more!&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SvP4cLJ1YXI/AAAAAAAAIeo/Ms8GO0bFt60/s1600-h/style.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SvP4cLJ1YXI/AAAAAAAAIeo/Ms8GO0bFt60/s400/style.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400933541061419378&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;/center&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-8017736035114997581?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'The Taj!', 'http://safety3rd.blogspot.com/2008/01/taj.html', 1, 'Alex', NULL, 'Agra', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (108, '2008-01-14 08:17:00', NULL, '&lt;center&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SvP6ADM6sxI/AAAAAAAAIfA/rcd7xdHQp2U/s1600-h/varanasi.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SvP6ADM6sxI/AAAAAAAAIfA/rcd7xdHQp2U/s400/varanasi.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400935256913785618&quot; /&gt;&lt;/a&gt;We have now reached the ancient city of Varanasi!&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SvP5_wVKjkI/AAAAAAAAIe4/-tspET_ZlfI/s1600-h/ganges.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SvP5_wVKjkI/AAAAAAAAIe4/-tspET_ZlfI/s400/ganges.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400935251848105538&quot; /&gt;&lt;/a&gt;Taking a morning boat ride on the Ganges&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SvP5_ps5VDI/AAAAAAAAIew/LSYWrnfUjjQ/s1600-h/sick.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SvP5_ps5VDI/AAAAAAAAIew/LSYWrnfUjjQ/s400/sick.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5400935250068591666&quot; /&gt;&lt;/a&gt;Lott is starting to feel sick (Logan was already sick in bed...)&lt;br /&gt;&lt;/center&gt;&lt;br /&gt;Sickness update: Logan seems to have mostly recovered, Lott''s not quite as far along, but should be on his way to getting better... Cyrus and I still healthy (knock on wood!). Tommy (from Chipatte Chasers) should be joining us here in Varanasi later this afternoon, we''re staying in town till tomorrow)&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-8696064344134467940?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Now in Varanasi!', 'http://safety3rd.blogspot.com/2008/01/now-in-varanasi.html', 1, 'Alex', NULL, 'Varanasi', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (109, '2008-01-15 01:41:00', NULL, 'To quote Mark Twain, &quot;Benares [Varanasi] is older than history, older than tradition, older even than legend, and looks twice as old as all of them put together.&quot; &lt;br /&gt;&lt;br /&gt;Also to quote Mark Twain, &quot;The reports of my death are greatly exaggerated.&quot;  Amazing what spending an entire day in bed will do for you.  Logan and I are alive and back in tip top shape.  Now if only Cyrus, Alex and Tommy would shake off their hangovers and get moving.&lt;br /&gt;&lt;br /&gt;P.S. Aunt Deb, the Mountain Man Mix was key in settling the stomach.&lt;br /&gt;P.P.S. Liv I didn''t drink the Ganges water, but I did touch it.  Not sure that helped.&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-4948362197875030396?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Varanasi Update', 'http://safety3rd.blogspot.com/2008/01/varanasi-update.html', 1, 'dlott', NULL, 'Varanasi', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (110, '2008-01-17 16:49:00', NULL, 'So far, the most time we&acirc;??ve spent in a given location has been approximately 24 hours, in Agra&acirc;?&brvbar; and then we got to Varanasi, and spent almost an entire two days there! And Varanasi is a fascinating place, a Hindu cultural and religious center, but we had been planning on spending  just a few hours there to see the people at the river-side Ghats&acirc;?&brvbar; So, what happened? First, we had made plans to meet up with Tommy (the one remaining Chapati Chaser) in Varanasi, but his rickshaw continued to have an unimaginable litany of problems, so he ended up being delayed by half a day&acirc;?&brvbar; But that didn&acirc;??t actually slow us down, as by then we were already committed to spending an extra day in Varanasi as the Dave&acirc;??s (Lott and Logan) were in no mood to just get out of bed, let alone get into a rickshaw, as they were recovering from food poisoning&acirc;?&brvbar; Finally, on Tuesday (Jan 15), we seemed ready to go, but needed to go by a service station to finish some repairs on Tommy&acirc;??s car, which was, of course, promised to be a quick operation, and ended up lasting from 9AM till 3PM! But, late Tuesday afternoon, we were finally set to go!&lt;br /&gt;&lt;br /&gt;&lt;a href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/R4-H_lDaOWI/AAAAAAAADO4/braTNUX_NBM/s1600-h/door.jpg&quot;&gt;&lt;img style=&quot;float:left; margin:0 10px 10px 0;cursor:pointer; cursor:hand;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/R4-H_lDaOWI/AAAAAAAADO4/braTNUX_NBM/s400/door.jpg&quot; border=&quot;0&quot; alt=&quot;Our ''door''&quot;id=&quot;&quot; /&gt;&lt;/a&gt; &lt;br /&gt;&lt;br /&gt;So, what did Cyrus and I do in Varanasi, while not bed-ridden or struggling with a mal-functioning rickshaw? Well, aside from gazing at the spectacle of, I&acirc;??m not sure what, that is Varanasi, we did a few productive things: bought a few blankets to make it a bit warmer in the rickshaws before reaching the Nepali mountains, I built a &acirc;??door&acirc;?? in ours, out of plastic and duct tape to shield against the wind a little better, and having learned our lesson from the day before, when Cyrus and Logan&acirc;??s  rickshaw died from a dirty sparkplug, I picked up a pair of spare spark plugs&acirc;?&brvbar; Back to Tuesday: having left Varanasi, we had a rather reasonable goal of reaching the famous Buddhist shrine of Bodhgaya (where Buddhism originated). This was a fairly reasonable 250 km away, so we were well on our way, even with a late start&acirc;?&brvbar; until about 7PM, when our rickshaw promptly died. Having now learned from the local mechanics that most rickshaw problems are caused by either the gas supply system or the spark plug, we didn&acirc;??t waste any time in taking our old spark plug out, and replacing it with one of the spares. This worked great&acirc;?&brvbar; for a couple of hours, when the rickshaw died again&acirc;?&brvbar; This time we managed to verify that the fuel was, in fact getting delivered, the carburetor was spotlessly clean, the air filter was clean&acirc;?&brvbar; and the spark plug? Well, we didn&acirc;??t have to check that since we just replaced it, right? Having exhausted all the options we could think of (and watching Logan fall asleep in his rickshaw as the clock wound past midnight), we gave up and decided to simply tow the rickshaw to the next town and deal with it in the morning.&lt;br /&gt;&lt;br /&gt;&lt;a href=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/R4-IP1DaOXI/AAAAAAAADPA/WJIuWt1Qv8s/s1600-h/hole.jpg&quot;&gt;&lt;img style=&quot;float:left; margin:0 10px 10px 0;cursor:pointer; cursor:hand;&quot; src=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/R4-IP1DaOXI/AAAAAAAADPA/WJIuWt1Qv8s/s400/hole.jpg&quot; border=&quot;0&quot; alt=&quot;The hole in the piston!&quot;id=&quot;BLOGGER_PHOTO_ID_5156489903883303282&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;In the morning, we first discovered some good news: there was an actual Bajaj dealership/service center just across the street from our hotel! Then we found some bad news: for all the things that are wrong with the state of Bihar, they have gotten some things right: 2 stroke rickshaws (like ours) are not sold in Bihar, which is great for the environment, but not so great for us, since this means our mechanic doesn&acirc;??t generally deal with rickshaws like ours and doesn&acirc;??t have a lot of spare parts for it available. And then, we found the really bad news: the reason our rickshaw had stopped running was that there now was a sizeable hole in our piston! Rewinding, it quickly became apparent that the spare spark plugs I had purchased did not fit our engine, and after pounding on the piston for about two hours, the spark plug had burst a hole in it&acirc;?&brvbar; (Lesson learned: just because the guy at the shop swears that he knows what he&acirc;??s doing and that the spark plug you had asked for will fit your engine, don&acirc;??t believe him! Double check then! And triple check again, before actually installing the sparkplug!). On the bright side, we finally knew what the problem was and could start trying to solve it &acirc;?? the service center, of course, did not have the specific part we needed, but eventually found a similar one that fit, and over 4 hours and 1600 Rupees (~$50) later, our rickshaw was alive again! This, finally (finally!) allowed us to get back on the road, and by 5:30, we reached Bodhgaya.&lt;br /&gt;&lt;a href=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/R4-IQFDaOZI/AAAAAAAADPQ/LLz0ZNb1Uy4/s1600-h/temple.jpg&quot;&gt;&lt;img style=&quot;float:left; margin:0 10px 10px 0;cursor:pointer; cursor:hand;&quot; src=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/R4-IQFDaOZI/AAAAAAAADPQ/LLz0ZNb1Uy4/s400/temple.jpg&quot; border=&quot;0&quot; alt=&quot;The temple at night&quot;id=&quot;BLOGGER_PHOTO_ID_5156489908178270610&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;This was largely the end of the adventure for the day, as we simply went  sight-seeing visiting the famous temple, getting immersed in a sea of Buddhist monks from every corner of the universe that were there (I hear Buddha&acirc;??s actual birthday is this week, so it must be an especially busy time). Thursday, with a three-rickshaw caravan fully intact once more, we were finally able to get an early start to the day (left at 6:15 AM) and head off towards Darjeeling and the Nepalese border!&lt;br /&gt;&lt;br /&gt;As I&acirc;??m typing this, the caravan is somewhere on the way to Darjeeling, I however, split off from them just before 1 in the afternoon in order to go to Dehli for a former co-worker&acirc;??s wedding reception on Saturday (so I don&acirc;??t really know how far they&acirc;??ve gone, but I suspect I would&acirc;??ve heard by now if something had gone seriously wrong). Sunday, fly back from Dehli to Darjeeling and meet back up with the crew! Currently, sitting in my hotel room in Patna, the capital of Bihar, and a wholly unremarkable city &acirc;?? will try to find some sights to see early tomorrow before catching a flight to Dehli in the evening, where I meet up with Michelle again, and we&acirc;??re both off to the reception on Saturday, assuming everything goes to plan!&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-4341048581371122159?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Varanasi and beyond', 'http://safety3rd.blogspot.com/2008/01/varanasi-and-beyond.html', 1, 'Alex', NULL, 'Varanasi, Gaya', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (111, '2008-01-18 07:06:00', NULL, '6AM Friday morning, in my reasonably comfortable hotel room in the reasonably uninteresting city of Patna, Bihar: my phone rings... Wondering if maybe it''s my parents not quite getting the time difference right, decide to pick it up: turns out it''s only Cyrus... clearly unaware that I have little desire to be awake at 6 in the morning, while away from the rickshaws! So, an update on the caravan: after dropping me off in the afternoon. continued to make good progress towards Darjeeling, and stopped for the night in Purnia. No problems with the rickshaws, and will probably be reaching Darjeeling/the border crossing later today. Depending on how much time they want to spend in Darjeeling, I''ll fly back from Dehli either to Darjeeling or to somewhere in Nepal to meet back up with them (the second option involves Lott being able to take our rickshaw into Nepal without me there, which should work, but may bring up some extra questions...)&lt;br /&gt;&lt;br /&gt;In the mean time, I''m in Putna. Getting to Putna involved navigating the Indian railway system, which is actually not as terrible of an experience as one might expect (if you''re only on the train for three hours). Met a guy there traveling all across the country from Nagaland, in the very, very eastern part of the coutry all the way to Mumbai - that''s a multi-day journey I don''t want to try... The guy, Imtinungkum was actually quite interesting - he is a Christian missionary (Nagaland is mostly Christian) working in Mumbai, and was just on the way back after spending Christmas back home. They also don''t speak Hindi in in Nagaland, so he was also using mostly English to communicate with the others on the train... And he spoke rather good English. And was appropriately surprised to hear that I was in the midst of driving an auto-rickshaw across India, much like everyone else here!&lt;br /&gt;&lt;br /&gt;Now finally in Putna... Lonely Planet isn''t particularly impressed with the city, struggling to find a couple of attractions to see - one being a granary built by the British over two centuries ago, which should offer great views of the city and the Ganges river, but instead was offering great views of the haze covering the city and the river this morning! (not sure how much of it was pollution and how much was just clouds, but I''m fairly certain it was a combination). The other is a Sikh temple, which I may still go to, but it''s 11km away, so it''s a fairly long ways away... Then again, I don''t have much else to do right now! 7:30 tonight, fly to Dehli. On one hand though, Putna is quite refreshing - after the tourist meccas of Varanasi of Bodhgaya, it''s nice to not be immersed in a sea of beggars, eager salesmen, and competing rickshaw drivers (actually those are still here...). But I have found that most people that say ''Hello'' out here really are just curious to see a tourist, as opposed to trying to sell me something...&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-8243173205795979496?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Patna', 'http://safety3rd.blogspot.com/2008/01/patna.html', 1, 'Alex', NULL, 'Patna', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (112, '2008-01-19 03:54:00', NULL, 'We are at this old Victorian hotel in Darjiling recovering from the long trip across India.  Hopefully we''ll enter Napal tonight and be in Katmandu in two days time.&lt;br /&gt;&lt;br /&gt;Reguardless I''m now looking forward to the announcement in 20 years that they can grow me a new set of lungs because I''m starting to think all the polution might have done some real damage.  Oh well.  Who needs to breath anyway..&lt;br /&gt;&lt;br /&gt;See you all soon.  Hopefully we''ll get some more pictures up shortly.&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-363317699535127906?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'At the Elgin in Darjiling, Looking forward to a new set of lungs.', 'http://safety3rd.blogspot.com/2008/01/at-elgin-in-darjiling-looking-forward.html', 1, 'Cyrus', NULL, 'Darjeeling', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (113, '2008-01-19 04:56:00', NULL, '&lt;div&gt;Seems to be the word most frequently thrown about to describe today.&lt;/div&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;It started at 0530 as we tried to clean the room of the last shards of glass from the case of beer we dropped the night before. We wanted to be gone before they discovered that we used the curtain to mop up the mess, and fortunately the guys with guns in the lobby had made sure our rickshaws didn''t &quot;wander off&quot; overnight. There was a heavy fog, but we were itching to get out of Bihar so we decided to press on. We encountered a toll both that had been staffed by some friendly locals. Normally we run such setups, and Tommy did, bringing one of the locals along with him who''d grabbed the center pillar. Since Slepak''s bags were in the back seat due to the broken roof rack, I decided that 10 Rs. was a reasonable bribe for them to ask and paid it to save Slepak''s bags.&lt;/div&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;The roads were pretty good and we could make about 30kph and still be able to see oncoming traffic. The tricky bit is that some of that traffic is ox drawn carts which are really slow (and don''t have lights). The lorries can''t really pass them so they end up going slow too and you''re really tempted to pass them. But you can''t see oncoming traffic. But Tommy''s leading. And he''s crazy (rode a motorcycle from London to Mongolia) And no one wants to be alone in the fog in Bihar. So you assume when Tommy passes someone, it''s safe for you to follow. And then he swerves left way too quickly and you see the close set headlights of a tractor hauling a cartload of sugarcane. And then you find your brakes are surprisingly effective as all three wheels lock up and you frantically skid sideways trying to tuck in behind the lorry and miss the tractor. And you give the guys in the chase rick a heart attack.&lt;/div&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;We decided to stop for breakfast, of amazing puri, to let the fog clear and lecture Tommy on passing etiquette. Neither worked. Fortunately the finished portion of the road ended and passing on broken dirt went much more sedately. It also kicked up a lot of dust which turned to mud on the windshields.&lt;/div&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5157057153987728018&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://3.bp.blogspot.com/_yG3LJbOEQIc/R5GMKIXTUpI/AAAAAAAABpM/xLrYgXVmkOc/s400/directions.jpg&quot; border=&quot;0&quot; /&gt;&lt;/div&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;Eventually the fog burned off and the road returned to being paved and we sped on to guided by by helpful signs like the above. A quick stop by the Bajaj Service Center to tighten the muffler yet again, and an entertaining railway crossing where a 5 year old was walking around with a big snake in a basket offering to let us take pictures (100 Rs, sorry no pictures) and hit the mountains. &lt;/div&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;The road to Darjeeling tenuously clings to the hill side. On your left, there''s a sheer drop to terraced tea plantations. On your right, there are jungles. A railway line weaves back and forth across the road as you drop the rickshaw into third gear, open the throttle and race up. Along the way all the locals smiled and waved. With the sun streaming it was one of the top 3 all time drives we''ve been on.&lt;/div&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5157057149692760690&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://2.bp.blogspot.com/_yG3LJbOEQIc/R5GMJ4XTUnI/AAAAAAAABo8/6AOGW3VM14w/s400/curve.jpg&quot; border=&quot;0&quot; /&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5157057153987728002&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://3.bp.blogspot.com/_yG3LJbOEQIc/R5GMKIXTUoI/AAAAAAAABpE/uHsB3lovQbM/s400/cyrus.jpg&quot; border=&quot;0&quot; /&gt;&lt;/div&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;By 4:30 we had crested 7400 feet and severely damaged two rickshaws (broken fuel and battery mounts) and ended up in Darjeeling proper. We had High Tea at the Windemere and checked into the Hotel Elgin for the evening. After hot showers, a delicious dinner and scotches in front of the fire, we closed the day. &lt;/div&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5157057158282695330&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://4.bp.blogspot.com/_yG3LJbOEQIc/R5GMKYXTUqI/AAAAAAAABpU/eM6b0_G8G2s/s400/elgin.jpg&quot; border=&quot;0&quot; /&gt;&lt;/div&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-3008726038554374430?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Day 18: EPIC', 'http://safety3rd.blogspot.com/2008/01/day-18-epic.html', 1, 'dlott', NULL, 'Darjeeling', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (114, '2008-01-20 06:20:00', NULL, 'After seeing former co-workers Michelle and Gitika in Dehli yesterday, I''m off to meet back up with the rickshaws once more! So far, I''ve had an only marginally delayed (1.5 hrs due to fog) flight into Kathmandu, now waiting for a 30 minute intra-Nepal flight to Jankpur, where I should be meeting back up with the rickshaw caravan later this afternoon. In the mean time, apparently the domestic terminal at the Kathmandu airport has a cyber cafe! And it even lets me upload my pictures...&lt;br /&gt;&lt;br /&gt;Reception:&lt;br /&gt;&lt;a href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/R5Lqg1DaOaI/AAAAAAAADPY/9BW10tMqNFU/s1600-h/temp+001.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/R5Lqg1DaOaI/AAAAAAAADPY/9BW10tMqNFU/s400/temp+001.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5157442373010733474&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;In the mean time, first impressions of the little that I''ve seen of Kathmandu - it''s a nice breather from India! It seems so much more serene, quiet, and clean(!). Plus you get unbeatable views of the Himalayas! The outlook on the mountains on the flight over were amazing!&lt;br /&gt;&lt;br /&gt;The mountains, as seen from my flight:&lt;br /&gt;&lt;a href=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/R5LqhFDaObI/AAAAAAAADPg/CprwRXb-2ZE/s1600-h/temp+004.jpg&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;&quot; src=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/R5LqhFDaObI/AAAAAAAADPg/CprwRXb-2ZE/s400/temp+004.jpg&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5157442377305700786&quot; /&gt;&lt;/a&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-2341622178984463218?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Meeting back up', 'http://safety3rd.blogspot.com/2008/01/meeting-back-up.html', 1, 'Alex', NULL, 'Dehli, Kathmandu, Janakpur', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (115, '2008-01-23 08:05:00', NULL, 'THEY ARE THROWING BRICKS AT US!&lt;br /&gt;&lt;br /&gt;Yesterday we got up early in Kathmandu and decided to go return our rickshaws to the storage depot near the airport.  So the five of us (Dave, Dave, Alex, Tommy, and I) got in our three rickshaws and headed over to the Rum Doodle Bar (40,000 &Acirc;&frac12; feet) to work out what we needed to do to return the rickshaws. &lt;br /&gt;&lt;br /&gt;There we met up with two other teams Discovering Earth who we had ran into quite a few times and a team of absolutely insane Brits one of who decided to return their rickshaw with us.  So the 8 of us started driving convoy style through Kathmandu following our carefully planned out route.&lt;br /&gt;&lt;br /&gt;Random Fact:  Less than a week ago the government of Napal raised the price of gas about 20%&lt;br /&gt;&lt;br /&gt;We made if a few blocks when we hit the first protest.  We looked at our map and rerouted.  Made it a few more blocks hit protest number two.  Rerouted again.  and again.&lt;br /&gt;&lt;br /&gt;Finally we stopped and after much work convinced a cabbie to park his cab and jump in one of our rickshaws and get us past the protests.  He was certain there wouldn''t be protesters on the big ring roads if we could just get out to them.  We then ran into two more groups of protesters who the cabbie talked to briefly and they moved stuff out of the way so we could get through.  Most of the protesters went out of their way to be nice to tourists and went through what appeared to be the last protest before the ring road my rickshaw was nailed in the back by a brick. &lt;br /&gt;&lt;br /&gt;Random Note: Most protests we encountered were just folks blocking the road and burning things.  Small bushes, limbs of trees, tires etc. and chanting something that I assume translates to &quot;Down with the King&quot; but I can''t say I''ve learned much Nepalese since I''ve been here.&lt;br /&gt;&lt;br /&gt;Logan and I were a bit put out by the rude folks throwing large rocks at our rather flimsy go cart but we were driving away from the at about 50km and hour and they couldn''t catch us.  We had less than 2km to go and we were finally on the &quot;Safe&quot; ring road.&lt;br /&gt;&lt;br /&gt;Random Fact: At this point we are in position 4 in the convoy.  It''s order was (Tommy + Cabbie, Discovering Earth, Crazy Brits, Us, Lott + Slepak)&lt;br /&gt;&lt;br /&gt;We came around a bend in the ring road and turned down a hill towards what appeared to be the largest protest we had seen that day.  The cabbie told Tommy to go ahead that they wouldn''t bother us.  So all of us went heading right for the protest.  Time compressed at this point and I''m not sure how everything that follows went down.  The crowd caught up with us and they was a group of people throwing rocks at the rickshaw and kicking it while I was trying desperately to turn it around.  Finally I just floored it and tried to make all 7hp of that thing take off even though I hadn''t made it all around and there were protesters in front of me.  They got out of the way though.  Discovering Earth stalled and was the last ones out.  Oddly Tommy was suspiciously the first one out and as we were flying up the hill in flee mode he was sitting out of his tuk tuk taking pictures of us getting attacked (Film camera, we''ll get copies someday)&lt;br /&gt;&lt;br /&gt;After that we ducked into the airport with it''s significant military presence and parked our rickshaws to regroup and settle down. &lt;br /&gt;&lt;br /&gt;THEY WERE THROWING BRICKS AT US!&lt;br /&gt;&lt;br /&gt;After consulting with not less than 20 people including military, random locals, cabbies, UN Folks, and airport officials we came to the conclusion that we had to wait it out at the airport.  They almost let us just drive across the cargo area to get where we were going but one of the chief types at the airport realized this would violate international air laws.  So we did what we''ve always done in the face of adversity. &lt;br /&gt;&lt;br /&gt;We went to the bar.&lt;br /&gt;&lt;br /&gt;We got out of there about 3.5 hours later once the protests had broken up and drove to the drop off point. &lt;br /&gt;&lt;br /&gt;Random fact: Team Dave, and I are presently at the airport getting out of this country.  As a rule of thumb I don''t hang out in countries where the locals have tried to kill me.  Protests were still going today and a general strike also started.  Lott and I are in the Thai Airways lounge which might be the best place in the city to hold up and wait to get out.  Logan is downstairs working on getting a ticket but sadly the airport is closed due to weather and he can''t buy a ticket until the weather lifts.  But we expect him to join us here in the next couple hours.&lt;br /&gt;&lt;br /&gt;Alex and Tommy have decided that they''ll just wonder up to the Everest base camp in the middle of winter.  We hope to hear from them again.&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-5608560367776299351?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Stoned in Kathmandu', 'http://safety3rd.blogspot.com/2008/01/stoned-in-kathmandu.html', 1, 'Cyrus', NULL, 'Kathmandu', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (116, '2008-01-23 15:32:00', NULL, 'Or at least, Team Dave is now back in India. Team Alex and Tommie are still in Nepal and hopefully have now acquired lots of new warm clothing and other gear for the treak to Everest base camp over the next few days. And Team Cyrus has now headed off to a warm beach to let his lungs recover.&lt;br /&gt;&lt;br /&gt;In case anyone was curious after the last post, I was finally able to purchase a ticket to Dehli on Jet Lite airlines which oddly enough I had to purchase from the lady at the Jet Airways counter. I''m now at the hotel internet machine in Dehli using the USB port to recharge the cell phone with India SIM I''m borrowing from Cyrus, and windows just reminded me that I have a few pictures of the train in Darjeeling on there.&lt;br /&gt;&lt;br /&gt;All along the winding mountain road up to Darjeeling there was a very narrow train track. At first we weren''t sure what it was used for, as it seemed too narrow for any real passengers. The cars are significantly larger than the train track would make you think, and it turns out there is in fact a steam engine that runs back and forth between Darjeeling, Ghum, and various other towns on the mountain a few times a day. The train came by while Tommie and I were at the welders getting the rickshaws fixed, and I didn''t have my main camera on me, but the phone seems to have captured the scene pretty well:&lt;br /&gt;&lt;br /&gt;&lt;a href=&quot;http://4.bp.blogspot.com/_h7P9YS3eqpU/R5dsFAZehFI/AAAAAAAAAA8/lalIy7Zgn-A/s1600-h/01-19-08_1140.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5158710731438195794&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://4.bp.blogspot.com/_h7P9YS3eqpU/R5dsFAZehFI/AAAAAAAAAA8/lalIy7Zgn-A/s320/01-19-08_1140.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a href=&quot;http://1.bp.blogspot.com/_h7P9YS3eqpU/R5dsFQZehGI/AAAAAAAAABE/VRu8i35BShw/s1600-h/01-19-08_1141.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5158710735733163106&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://1.bp.blogspot.com/_h7P9YS3eqpU/R5dsFQZehGI/AAAAAAAAABE/VRu8i35BShw/s320/01-19-08_1141.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a href=&quot;http://2.bp.blogspot.com/_h7P9YS3eqpU/R5dsFgZehHI/AAAAAAAAABM/_d31Uw_twbQ/s1600-h/01-19-08_1259.jpg&quot;&gt;&lt;img id=&quot;BLOGGER_PHOTO_ID_5158710740028130418&quot; style=&quot;DISPLAY: block; MARGIN: 0px auto 10px; CURSOR: hand; TEXT-ALIGN: center&quot; alt=&quot;&quot; src=&quot;http://2.bp.blogspot.com/_h7P9YS3eqpU/R5dsFgZehHI/AAAAAAAAABM/_d31Uw_twbQ/s320/01-19-08_1259.jpg&quot; border=&quot;0&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;div&gt; &lt;/div&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;&lt;/div&gt;&lt;br /&gt;&lt;br /&gt;&lt;div&gt;&lt;/div&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-8246779322506329033?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Back to India', 'http://safety3rd.blogspot.com/2008/01/back-to-india.html', 1, 'Logan', NULL, 'Darjeeling', 1, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (117, '2008-01-25 05:36:00', NULL, 'I can confirm, Tommy and I are, in fact, trekking our way up to the Everest Base Camp! So far in the second day, and have found an internet cafe in Namche Bazar, Nepal. Elevation: 3440m! I''m going to guess this may very well be the highest internet cafe in the world...&lt;br /&gt;&lt;br /&gt;We''re in day 2 so far, progressing reasonably well, trying not to rush to avoid altitude sickness. The views started being amazing on the flight over, and haven''t gotten any worse since! Will be posting lots of pictures, hopefully once we''re back down - current ETA for back in Kathmandu is Feb 7...&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-7958478170522042449?l=safety3rd.blogspot.com''/&gt;&lt;/div&gt;', NULL, NULL, 'Up to Everest Base Camp', 'http://safety3rd.blogspot.com/2008/01/up-to-everest-base-camp.html', 1, 'Alex', NULL, 'Namche Bazar', 1, NULL, NULL);
