INSERT INTO `rr2008_msgs` VALUES (192, '2009-12-25 15:53:26', NULL, ' ', NULL, NULL, 'Philippines', 'http://www.facebook.com/album.php?aid=2045016&id=1046387715&l=d4ad8ef6c6', 2, 'Alex', NULL, 'Manila, Sabang, Port Barton, El Nido, Taytay', 2, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (193, '2009-12-27 10:42:05', 'Hong Kong, Hong Kong', 'horse racing and ultimate frisbee on a drizzly day in HK - excellent day, the rains be damned!', 22.3964, 114.109, NULL, NULL, 0, NULL, NULL, NULL, 2, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (194, '2009-12-27 15:30:00', NULL, 'If Paris is the City of Light, then Hong Kong is certainly the City of Lights, as in lots and lots of brightly-colored neon lights that burn through the night.&lt;br /&gt;&lt;center&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzeIMNhzoCI/AAAAAAAAIqQ/SIjMSLALt5U/s1600-h/whitetop.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzeIMNhzoCI/AAAAAAAAIqQ/SIjMSLALt5U/s400/whitetop.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950419934945314&quot; /&gt;&lt;/a&gt;A skyscraper lit up at night&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SzeILgfQqRI/AAAAAAAAIqI/zphwkLsKqpU/s1600-h/peak.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SzeILgfQqRI/AAAAAAAAIqI/zphwkLsKqpU/s400/peak.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950407844669714&quot; /&gt;&lt;/a&gt;A night-time overview of the city&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzeIlzs5EUI/AAAAAAAAIrQ/nWnaF8Frv1o/s1600-h/fountain.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzeIlzs5EUI/AAAAAAAAIrQ/nWnaF8Frv1o/s400/fountain.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950859678716226&quot; /&gt;&lt;/a&gt;And a nicely lit fountain near the waterfront&lt;br /&gt;&lt;/center&gt;&lt;br /&gt;There''s really not a lot of places I''ve seen that can rival the sheer magnitude and audacity of the neon here. Las Vegas, centered on its big casinos, seems downright organized, almost subdued, in comparison. Tokyo''s Shinjuku and Times Square in New York are probably the best comparisons&lt;br /&gt;&lt;center&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SzeIMzOHibI/AAAAAAAAIqg/toL1gu1hiak/s1600-h/nathan.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SzeIMzOHibI/AAAAAAAAIqg/toL1gu1hiak/s400/nathan.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950430052911538&quot; /&gt;&lt;/a&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzeIWi36zmI/AAAAAAAAIqo/quMue8KEHa0/s1600-h/nathan2.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzeIWi36zmI/AAAAAAAAIqo/quMue8KEHa0/s400/nathan2.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950597463527010&quot; /&gt;&lt;/a&gt;An overabundance of advertising just off of the main thoroughfare of Nathan Road&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SzeImV-JMpI/AAAAAAAAIrg/vK6NUl1s08E/s1600-h/bus.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SzeImV-JMpI/AAAAAAAAIrg/vK6NUl1s08E/s400/bus.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950868877882002&quot; /&gt;&lt;/a&gt;Nathan Road itself&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SzeIMT0ogwI/AAAAAAAAIqY/Po67JVWSAEQ/s1600-h/fish.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SzeIMT0ogwI/AAAAAAAAIqY/Po67JVWSAEQ/s400/fish.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950421624521474&quot; /&gt;&lt;/a&gt;It''s a neon arms race out here - if you don''t advertise your restaurant with a gigantic neon sign, your competitors will just drown you out with theirs&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzeIlfT3hkI/AAAAAAAAIrI/QjitVJrJQUY/s1600-h/peninsula.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzeIlfT3hkI/AAAAAAAAIrI/QjitVJrJQUY/s400/peninsula.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950854205048386&quot; /&gt;&lt;/a&gt;Admittedly, the city is even more lit up than normal (even if that does seem virtually impossible) around Christmas&lt;br /&gt;&lt;/center&gt;&lt;br /&gt;Hong Kong, generally, is two very distinct cities, separated by Victoria Harbour. There is Hong Kong island - the major financial and business center, with its gigantic high-rises, upscale restaurants, and the atmosphere more reminiscent of New York and London than any part of Asia. And this is really what I had expected of Hong Kong. But, there is the other side of the harbor: Kowloon City and Tsim Sha Tsui - this is the busy, bustling, crowded, fragrant (smelly!) part of town on the peninsula that''s connected to mainland China. And it''s connected in more ways than one - it''s unmistakably Asian. From the ''wet markets'' selling fish that''s been caught, but hasn''t been killed yet to the ''No Hawking'' signs on the waterfront, which are quite necessary, as a block away, there are crowds of people trying to sell you anything you can ever imagine. I wouldn''t say wish for, because I don''t actually wish for a suit or a Rolex knock-off, but I keep being offered them anyway... And, of course, at night, both sides of the harbor are brightly lit up putting up a light show of proportions that can only be measured in Megawatts. Gigawatts?&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SzeIXqGo2TI/AAAAAAAAIrA/mFZ3W35UrYM/s1600-h/riverbank.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SzeIXqGo2TI/AAAAAAAAIrA/mFZ3W35UrYM/s400/riverbank.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950616584182066&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SzeIXb8ZhtI/AAAAAAAAIq4/zlhuHCY5F54/s1600-h/river2.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/SzeIXb8ZhtI/AAAAAAAAIq4/zlhuHCY5F54/s400/river2.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950612783138514&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzeIWwOUfbI/AAAAAAAAIqw/zbBZvvnPxbM/s1600-h/river.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzeIWwOUfbI/AAAAAAAAIqw/zbBZvvnPxbM/s400/river.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950601047145906&quot; /&gt;&lt;/a&gt;So, my first impressions of Hong Kong? Well, the very first impression was that it smells! Or specifically, the rooms I had on my first two nights smelled. Especially the first one, but I arrived at 3 in the morning, so I didn''t care all that much. I promptly moved to a guesthouse in the ubiquitous Chungking Mansions, where my first room also smelled (but less pungently), and by night 3, I was in an odor-free environment! Chunking Mansions itself defies description - it is a 15 story apartment complex, where each apartment has been converted into a hotel/guesthouse - it is the budget accommodation in the city. It is also quite old, home to a variety of small Indian and Pakistani restaurants downstairs, and generally not all that well maintained. The clientele has a smattering of backpackers lost in a sea of mainland tourists and laborers from India, Pakistan, and a wide variety of other British colonies. This evening I ran into a couple from Bogota, Colombia too (who were trying to find an exit...) - it''s kind of like a model UN, except poorly-run former British colonies are over-represented.&lt;br /&gt;&lt;br /&gt;All that being said, I''m quite happy with the room, I''ve finally ended up with - it doesn''t smell, I have a window to the outside world (the previous one had a window also, but I''m not sure what it led to - certainly no the outside though), there''s hot water, and even constant and free internet access, so not all that bad. As for my overall impressions of Hong Kong - oddly enough, I actually found the place rather relaxing and a little lonely.&lt;br /&gt;&lt;br /&gt;The lonely is easy - yes, there''s a gigantic, vibrant mass of people populating this town at all hours of the day and night. And some (probably large) number of them are even fellow backpackers, however, lacking hostels and other such obvious ways of meeting other travelers, they immediately blend in and dissipate into the background, and you''re back on the street surrounded by locals, mainland tourists, and Indians offering fake Rolexes. Fortunately, I was able to solve this problem first by meeting up with Tim (he of wine tasting in Mendoza last March fame), who''s currently in town visiting family, and then further by finding a pickup frisbee game.&lt;br /&gt;&lt;br /&gt;As for relaxing? Well, admittedly, I''m certainly in the minority in finding Hong Kong relaxing, but honestly, my concerns in the Philippines included:&lt;br /&gt; - there''s Malaria on Palawan, have I taken my malaria pills today yet?&lt;br /&gt; - if I have a flight out of Puerto Princessa at 8, and the bus ride there is supposed to take 7 hours, how early should I leave to account for the inevitable delays and breakdowns?&lt;br /&gt; - the traffic in Manila is so bad, that getting to and from the airport requires budgeting a good two hours extra just for the traffic&lt;br /&gt;&lt;br /&gt;Those are potentially difficult problems. Here? The place is tiny - in 7 hours you''d be well into China no matter how many times your bus broke down. The traffic is awful too, but it makes no difference as there''s a very fast and efficient (and relatively inexpensive) subway system in place, not to mention the cross-harbor ferries, which don''t get you there as fast as the subway, but offer reat views of the harbor along the way. The biggest problem here has really been finding a place to stay that I liked, and I had a ready-made choice of 100 or so guesthouses just here in the Chungking Mansions, along with internet recommendations for which ones people have liked before. The runner-up problem is deciding if I should have Chinese, Japanese, Korean, or Indian food for dinner. Maybe it is just me, but I call this relaxing! As for the people persistently trying to sell you something, anything, on the streets (which has been know to annoy people before), I''ve learned to just take it all in stride by now and laugh about the fact that somebody thinks I''m a good candidate to try selling a Rolex and a suit to! It sure beats the army of Nigerians assaulting you with offers of marijuana and every other substance in the world on every street corner in Kathmandu, not to mention the guys in Cartagena, Colombia, who''ll hook you up with a tour, chicas, and cocaina. You decide the order in which you want to prioritize the three! Ok, to be entirely accurate, hashish is on offer here in Hong Kong too, but the would-be sellers, at least, seem a bit surreptitious about it.&lt;br /&gt;&lt;center&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SzeImOfhZ4I/AAAAAAAAIrY/EVOhbC8aHTU/s1600-h/alex.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SzeImOfhZ4I/AAAAAAAAIrY/EVOhbC8aHTU/s400/alex.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5419950866870396802&quot; /&gt;&lt;/a&gt;Relaxing in Hong Kong!&lt;br /&gt;&lt;/center&gt;&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-1786123329686197400?l=safety3rd.blogspot.com'' alt='''' /&gt;&lt;/div&gt;', NULL, NULL, 'The City of Lights', 'http%3A%2F%2Fsafety3rd.blogspot.com%2F2009%2F12%2Fcity-of-lights.html', 1, 'Alex', NULL, 'Hong Kong', 2, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (195, '2009-12-28 16:25:00', NULL, '&lt;span style=&quot;font-weight:bold;&quot;&gt;&lt;span style=&quot;font-style:italic;&quot;&gt;or Manila vs. Hong Kong: a study in contrasts&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;Manila is the capital of the Philippines, a nation that had been a Spanish colony for three centuries until gaining independence at the start of the 20th century. Hong Kong has been a colony of the United Kingdom since being ceded to the British crown by the Chinese emperor in the middle of the 19th century and until it was formerly handed back over in 1997. Both cities lie in South East Asia, straddling the two shores of South China Sea, and share an extensive history of European influence, so they should have some similarities based on their colonial pasts? Well...&lt;br /&gt;&lt;center&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/Szji-aCBFWI/AAAAAAAAIsw/nsHfVCRb3Bk/s1600-h/2towers.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/Szji-aCBFWI/AAAAAAAAIsw/nsHfVCRb3Bk/s400/2towers.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420331713307940194&quot; /&gt;&lt;/a&gt;This is Hong Kong&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/Szji9sFxuVI/AAAAAAAAIsg/gy-LIZsVTts/s1600-h/junk.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/Szji9sFxuVI/AAAAAAAAIsg/gy-LIZsVTts/s400/junk.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420331700975679826&quot; /&gt;&lt;/a&gt;So is this - modernity with a slight nod to old Chinese culture&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/Szji-AKhBEI/AAAAAAAAIso/q59oB5hFsho/s1600-h/greetings.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/Szji-AKhBEI/AAAAAAAAIso/q59oB5hFsho/s400/greetings.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420331706364265538&quot; /&gt;&lt;/a&gt;Season''s Greetings, celebrating a Christian holiday, Hong Kong, however, remains almost strictly all business, as the tower in the background might suggest&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/Szjiuqij0XI/AAAAAAAAIro/23Ye6Ah_9dw/s1600-h/augustin.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 300px; height: 400px;&quot; src=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/Szjiuqij0XI/AAAAAAAAIro/23Ye6Ah_9dw/s400/augustin.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420331442861494642&quot; /&gt;&lt;/a&gt;This, on the other hand, is Manila. The St. Augustin Church in the old town section of Intramuros.&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzjivvtEKDI/AAAAAAAAIsA/Su4tMm4LyUg/s1600-h/santiago.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 300px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzjivvtEKDI/AAAAAAAAIsA/Su4tMm4LyUg/s400/santiago.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420331461427603506&quot; /&gt;&lt;/a&gt;Fort Santiago protecting Manila &lt;br /&gt;&lt;/center&gt;&lt;br /&gt;So, what does Hong Kong remind me of? Well, a little London, a little Shanghai, a little Singapore, a little New York - a mixture of East and West: a distinctly modern, developed, Western city with an unmistakably Eastern influence. What does Manila remind me of? Certainly nothing in Asia. It''s certainly closest to Cartagena, and Cuzco, and Panama City - other Spanish colonial capitals. The church plays a big role in former Spanish colonies, just as long as it''s the Roman Catholic church.&lt;br /&gt;&lt;center&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzjivW55lMI/AAAAAAAAIr4/mx4WJmDZc4s/s1600-h/catehdral.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 297px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzjivW55lMI/AAAAAAAAIr4/mx4WJmDZc4s/s400/catehdral.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420331454770549954&quot; /&gt;&lt;/a&gt;The Manila Cathedral - it may have been destroyed a few times (fire, earthquake, WWII), but it''ll always be rebuilt!&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SzjjVrJe-jI/AAAAAAAAIs4/a4sRnhA-88I/s1600-h/stjohn.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SzjjVrJe-jI/AAAAAAAAIs4/a4sRnhA-88I/s400/stjohn.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420332113039653426&quot; /&gt;&lt;/a&gt;Hong Kong has a cathedral too - St. John''s, an Anglican Cathedral, so not quite acceptable to the Roman Catholics, but close enough for our purposes&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SzjjWSsPGcI/AAAAAAAAItI/shLATfPNp40/s1600-h/temple.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/SzjjWSsPGcI/AAAAAAAAItI/shLATfPNp40/s400/temple.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420332123654396354&quot; /&gt;&lt;/a&gt;Hong Kong is a little more diverse - including several Buddhist temples, such as Sik Sik Yuen Wong Tai Sin, pictured here, nestled in between the sky scrapers&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzjivF5qABI/AAAAAAAAIrw/wxQMug1FUmI/s1600-h/inside.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 300px; height: 400px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzjivF5qABI/AAAAAAAAIrw/wxQMug1FUmI/s400/inside.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420331450206126098&quot; /&gt;&lt;/a&gt;Religious insignia inside the Manila Cathedral&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzjjWm7ZJAI/AAAAAAAAItQ/xPqtA0RlVeU/s1600-h/incense.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzjjWm7ZJAI/AAAAAAAAItQ/xPqtA0RlVeU/s400/incense.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420332129086678018&quot; /&gt;&lt;/a&gt;And incense burning in Hong Kong&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/Szjjgsn1SWI/AAAAAAAAItw/tgRxYN7C-wU/s1600-h/buddha.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/Szjjgsn1SWI/AAAAAAAAItw/tgRxYN7C-wU/s400/buddha.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420332302413941090&quot; /&gt;&lt;/a&gt;Want more religious symbols? How about a big Buddha on Lantau island&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzjjgRGO2UI/AAAAAAAAIto/H2sjr3yOhSo/s1600-h/buddhaclose.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzjjgRGO2UI/AAAAAAAAIto/H2sjr3yOhSo/s400/buddhaclose.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420332295025252674&quot; /&gt;&lt;/a&gt;There are no Buddha''s in the Philippines&lt;br /&gt;&lt;/center&gt;&lt;br /&gt;Well, how about just going out on the streets of the two cities? Perhaps some monuments to inspire the local population?&lt;br /&gt;&lt;center&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzjiwCxOKgI/AAAAAAAAIsI/YY34RiDqTGw/s1600-h/statue.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 300px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzjiwCxOKgI/AAAAAAAAIsI/YY34RiDqTGw/s400/statue.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420331466545310210&quot; /&gt;&lt;/a&gt;Manila. I don''t actually know whom this statue commemorates. I''m going to say she wasn''t that good at kung fu though...&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/Szji9GzZuVI/AAAAAAAAIsY/TMk6z5Tl1qU/s1600-h/bruce.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/Szji9GzZuVI/AAAAAAAAIsY/TMk6z5Tl1qU/s400/bruce.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420331690966497618&quot; /&gt;&lt;/a&gt;Hong Kong celebrates its own heroes - Bruce Lee could take the Spanish down all by himself if he wanted to!&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzjjgJBT5EI/AAAAAAAAItg/I7tONIVZHng/s1600-h/lantern.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://2.bp.blogspot.com/_-HBt9Acp_lc/SzjjgJBT5EI/AAAAAAAAItg/I7tONIVZHng/s400/lantern.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420332292857128002&quot; /&gt;&lt;/a&gt;Old and new juxtaposition in Hong Kong at the Kowloon Walled City Park&lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/Szji89OS9pI/AAAAAAAAIsQ/eeLmmXY9O_M/s1600-h/cemetery.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 300px; height: 400px;&quot; src=&quot;http://4.bp.blogspot.com/_-HBt9Acp_lc/Szji89OS9pI/AAAAAAAAIsQ/eeLmmXY9O_M/s400/cemetery.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420331688394946194&quot; /&gt;&lt;/a&gt;And something not entirely Spanish, or Catholic, in Manila - not Filipino either though: the Chinese cemetery. &lt;br /&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/Szjjf6b5MfI/AAAAAAAAItY/Y7tXyF7aGKc/s1600-h/frame.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://3.bp.blogspot.com/_-HBt9Acp_lc/Szjjf6b5MfI/AAAAAAAAItY/Y7tXyF7aGKc/s400/frame.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420332288942092786&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzjjV1h7KZI/AAAAAAAAItA/igK1aPMgpTM/s1600-h/peak.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 267px; height: 400px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/SzjjV1h7KZI/AAAAAAAAItA/igK1aPMgpTM/s400/peak.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5420332115826518418&quot; /&gt;&lt;/a&gt;A couple more sights from the streets of Hong Kong&lt;br /&gt;&lt;/center&gt;&lt;br /&gt;At this point I was going to make a complicated point comparing the different colonial approaches Britain and Spain had taken (Spain: full totalitarian control, flatly imposing Catholicism vs. Britain''s more freedom embracing approach, which allowed for the local religions to remain) and the results: the British colonies have, at times, achieved greater economic propsperity (Hong Kong, USA), but the Spanish ones have almost certainly ended up being more stable, easier to govern, and much more similar to the home country in the long run (South America may not seem particularly stable, but it''s got nothing on formerly British India and the Middle East). However, I don''t like making that point, and I don''t really know what I''m talking about when it comes to geo-political issues, and it''s not really a true apples-to-apples comparison - after all, The Incas and Aztecs in South America may have been the most advanced local cultures the Spanish had encountered (the Philippines didn''t have much of a developed nation-state prior to the Spanish), whereas the British colonies got to deal with long-established cultures like India, China, and the tribal regions of Afghanistan and Pakistan.&lt;br /&gt;&lt;br /&gt;So, instead, let''s just say the two European powers took very different approaches to running their colonies, and the results may be most obvious in the Philippines. This is a country in SE Asia, but it may as well be in Latin America. The currency is the peso, the language has a strong Spanish influence to it (mas barato por favor! = cheaper please! both here and in Bolivia...), the names of streets, towns, and people are often Spanish based (Fort Santiago, Puerto Princessa, etc.), and not only is Christianity (Catholicism especially, even though other branches are popping up, probably due to the last 100 years of American colonial influence) the undisputed state religion, the thing I found to be truly surprising was the almost complete absence of Buddhism, in a region where Buddhism dominates all the neighboring states. Islam, by the way, is present down South, stemming from nearby Malaysia and Indonesia, where Islam is the primary religion (even though there''s a lot more signs of Buddhism in both of those countries than there is in the Philippines). The opposing Muslims were apparently quite the annoying discovery to the invading Spanish a few centuries ago, as Spain had just finished fighting off the Muslim forces (the Moors) back in the Spanish homeland and in Northern Africa (this random historical footnote brought to you by Lonely Planet).&lt;br /&gt;&lt;br /&gt;Well, that''s probably enough geo-political musings and ramblings for the moment. Today is the last of my six here in Hong Kong - tonight I get on a plane bound for the beautiful beaches of Thailand to celebrate New Year''s in style. Goals for today are to find a replacement camera (still! Hong Kong''s been refusing to repair mine...) and meet up with Tim for more delicious local cusine - the food in Hong Kong is excellent as it is, but yesterday he and his parents took me out to a couple of really good local restaurants (that I would have never picked out myself), and the food gets to be even more amazing when you''re with people who know what they are talking about, as opposed to me just randomly pointing at things on a menu!&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-1112027835667018039?l=safety3rd.blogspot.com'' alt='''' /&gt;&lt;/div&gt;', NULL, NULL, 'Next time you need something colonized', 'http%3A%2F%2Fsafety3rd.blogspot.com%2F2009%2F12%2Fnext-time-you-need-something-colonized.html', 1, 'Alex', NULL, 'Manila, Hong Kong', 2, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (196, '2009-12-29 12:02:55', 'Hong Kong, Hong Kong', '@ HK airport, getting ready to fly to Phuket - getting excited about Thailand and the beaches!', 22.3964, 114.109, NULL, NULL, 0, NULL, NULL, NULL, 2, NULL, NULL);
INSERT INTO `rr2008_msgs` VALUES (198, '2009-12-29 18:42:09', 'Phuket City, Thailand', 'back in thailand! Just negotiated a middle of the night ride to the coast, i love that anything is possible in this country', 8.11871, 98.3107, NULL, NULL, 0, NULL, 423, NULL, 2, NULL, NULL);
