INSERT INTO `rr2008_msgs` VALUES (380, '2010-06-01 07:56:00', NULL, '&lt;a onblur=&quot;try {parent.deselectBloggerImageGracefully();} catch(e) {}&quot; href=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/TAS9xt_IfzI/AAAAAAAAJKo/spf3-hPTtuE/s1600/seven.JPG&quot;&gt;&lt;img style=&quot;display:block; margin:0px auto 10px; text-align:center;cursor:pointer; cursor:hand;width: 400px; height: 267px;&quot; src=&quot;http://1.bp.blogspot.com/_-HBt9Acp_lc/TAS9xt_IfzI/AAAAAAAAJKo/spf3-hPTtuE/s400/seven.JPG&quot; border=&quot;0&quot; alt=&quot;&quot;id=&quot;BLOGGER_PHOTO_ID_5477711708644998962&quot; /&gt;&lt;/a&gt;&lt;br /&gt;&lt;seven.jpg&gt;&lt;br /&gt;That''s right, I''ve finally made it to Africa, continent #7! Oh, and there''s some fairly large Pyramids around here too... Heading deeper into the continent, up the Nile into the center of Egypt tonight.&lt;div class=&quot;blogger-post-footer&quot;&gt;&lt;img width=''1'' height=''1'' src=''https://blogger.googleusercontent.com/tracker/2883997969551391628-327338907296515799?l=safety3rd.blogspot.com'' alt='''' /&gt;&lt;/div&gt;', NULL, NULL, 'Seven!', 'http%3A%2F%2Fsafety3rd.blogspot.com%2F2010%2F06%2Fseven.html', 1, 'Alex', NULL, 'Cairo', 3, NULL, NULL);
