INSERT INTO `rr2008_msgs` VALUES (543, '2008-02-03 08:54:56', NULL, ' ', NULL, NULL, 'Everest Base Camp', 'http://www.facebook.com/album.php?aid=2001636&id=1046387715&l=1698e56322', 2, 'Alex', NULL, 'Lukla, Namche Bazar, Tengboche, Dingbouche, Lobuche, Gorak Shep', 1, 'trekking up to over 5,000m! Yes, this is from three years ago - I felt the website was missing some exciting and dramatic pictures from the Himalayas, so I''ve gone back to add a few more to the album, which had a mere six previously! Which was clearly unacceptable...', '/img/menu/amudablam.jpg');
INSERT INTO `rr2008_msgs` VALUES (544, '2008-02-13 00:00:00', NULL, ' ', NULL, NULL, 'A few shots of Kathmandu', 'http://www.facebook.com/album.php?aid=2001678&id=1046387715&l=a1e7e99568', 2, 'Alex', NULL, 'Kathmandu', 1, 'After 10 days trekking through the Himalayas, it was time to return to Kathmandu... in hopes of finding civilization, of which I didn''t find a whole lot of here! (this album is getting a much needed refill, much like the Everest one - I know I called it ''a few shots of Kathmandu,'' but 3 was much too few!)', '/img/menu/ktm.jpg');
