INSERT INTO `rr2008_msgs` VALUES (546, '2011-01-07 00:00:00', NULL, ' ', NULL, NULL, 'The Wild, Wild West', 'http://www.facebook.com/album.php?aid=2098009&id=1046387715&l=ed199927a0', 2, 'Alex', NULL, 'San Diego, San Francisco, Lake Tahoe, Klamath Falls, Seattle, Denver, Custer State Park, Mount Rushmore, Devils Tower, Victoria, Anacortes', 3, 'From taking a train all the way up the West Coast to a voyage to Mount Rushmore, Crazy Horse, and Devil''s Tower out on the planes - the US and Canada, West of the Mississippi. This is technically combining several trips together from the last two years, but I feel they all fit in quite well!', '/img/menu/amtrak.jpg');
